mapper.select:
SELECT id, activity_code as activityCode, activity_type as activityType, activity_type as activityType, introduction as introduction, sales_start_time as salesStartTime, sales_end_time as salesEndTime FROM tb_promotion_base_info
deleted = 0
AND activity_code =#{activityCode}
AND sales_start_time >= #{begainTime}
AND sales_end_time <= #{endTime}
AND created_user = #{createdUser}
AND type = #{promotionType}
order by created_time desc

select a.activity_code as 'activityCode', a.activity_type as 'activityType', a.sales_start_time as 'salesStartTime', a.sales_end_time as 'salesEndTime', a.amount as 'amount', a.bill_cycle as 'billCycle', a.description as 'description', a.introduction as 'introduction', a.channel as 'channel', a.the_way as 'theWay', a.shared_activity as 'sharedActivity', a.selling_price as 'sellingPrice', a.bill_price as 'billPrice', a.handling_fee as 'handlingFee', a.tax_rate as 'taxRate', a.other as 'other', a.submit as 'submit', a.type as 'type', a.created_time as 'createdTime', a.created_user as 'createdUser', a.updated_time as 'updatedTime', a.updated_user as 'updatedUser', a.usage_start_time as 'usageStartTime', a.usage_end_time as 'usageEndTime', b.area, b.city, b.restaurant_code as 'restaurantCode', b.restaurant_name as 'restaurantName', a.contract_amount as 'contractAmount', a.prepayment_amount as 'prepaymentAmount', a.wage as 'wage', a.advertising_fee as 'advertisingFee', a.test_fee as 'testFee', a.coupon_fee as 'couponFee', a.coupon_effective_time as 'couponEffectiveTime', a.discount_fee as 'discountFee', a.bill_user_name as 'billUserName', a.bill_account_number as 'billAccountNumber' from tb_promotion_base_info as a left join tb_promotion_mapper as b ON a.activity_code=b.activity_code ANd a.deleted=0 AND b.deleted=0
a.activity_code in
#{valueList}
